// functions that take random qsts from each category and each difficulty
function easy_qsts_generator(){
    global $easy,$easy_cats,$easy_dogs,$easy_birds,$easy_qst;
    $easy_qst=array('');
    while (count($easy_qst)<4){
    $i= rand(0,(count($easy)-1));
    $j= rand(0, (count($easy_cats)-1));
    if ( !in_array($easy[$i][$j], $easy_qst)){
        array_push($easy_qst, $easy[$i][$j]);
    }
        
    }
    array_shift($easy_qst);
    //print_r($easy_qst);
    return $easy_qst;
}


function medium_qsts_generator(){
    global $medium,$medium_cats,$medium_dogs,$medium_birds,$medium_qst;
    
    $medium_qst=array('');
