}

function hard_qsts_generator(){
    global $hard,$hard_cats,$hard_dogs,$hard_birds,$hard_qst;
    
    $hard_qst=array('');
    while (count($hard_qst)<4){
    $i= rand(0,(count($hard)-1));
    $j= rand(0, (count($hard_cats)-1));
    if ( !in_array($hard[$i][$j], $hard_qst)){
        array_push($hard_qst, $hard[$i][$j]);
